mapper.select:
SELECT * FROM w_user
WHERE w_id = #{w_id}
  AND provider = #{provider}

mapper.update:
UPDATE w_user
SET w_nickname = #{w_nickname}
WHERE iuser = ${iuser}

UPDATE w_user
SET

    w_nickname = #{w_nickname}
,

    profileImg = #{profileImg}

WHERE iuser = ${iuser}

